mapper.update:
update t_dictionary
set name = #{name,jdbcType=VARCHAR},
  code = #{code,jdbcType=VARCHAR},
  subcode = #{subcode,jdbcType=VARCHAR},
  val = #{val,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, code, subcode, val
from t_dictionary
where id = #{id,jdbcType=INTEGER}
