mapper.update:
update t_permission
set pid = #{pid,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR},
  icon = #{icon,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, pid, name, icon, url
from t_permission
where id = #{id,jdbcType=INTEGER}

select id, pid, name, icon, url
from t_permission
where pid = #{pid}
